var mongoose = require('mongoose')
var Schema = mongoose.Schema
// 连接 mango 数据库
mongoose.connect('mongodb://localhost/mango')

// 设计表结构
var blogSchema = new Schema({
  title:  String,
  author: String,
  body:   String,
  comments: [{ body: String, date: Date }],
  date: { type: Date, default: Date.now },
  hidden: Boolean,
  meta: {
    votes: Number,
    favs:  Number
  }
});

var userSchema = new Schema({
  username: {
    type: String, 
    required: true
  },
  password: {
    type: String,
    required: true
  },
  email: {
    type: String
  }
})

// 将表结构发布为模型
/**
 * first param: 传入一个大写名词单数字符串，
 *              mongoose会自动转为小写复数的表名称
 *              例如：User 变为 users
 * second param：表结构
 * return value：模型构造函数
 */
var User = mongoose.model('User', userSchema)

// 创建对象
var user = new User({
  username: 'mango',
  password: '123',
  email: 'mango@163.com'
})

// ret 返回值， 返回保存后的对象
user.save((err, ret) => {
  if (err) {
    return console.log('保存失败')
  }
  console.log('保存成功')
  console.log(ret)
})

